import java.util.InputMismatchException; import java.util.Scanner; public class Convert {public static void main(String[] args) {boolean valid = false; while (!valid) {try {System.out.println("Choose Conversion:\nInfix->Postfix (1)\nPostfix->Infix (2)\nSolve Postfix (3)\nQuit (4)"); Scanner scan = new Scanner(System.in); int user = scan.nextInt(); MyStack fix = new MyStack(); if (user == 1) {String postFix = fix.toPost(); System.out.println("Postfix: "+postFix); System.out.println(); } if (user == 2) {String inFix = fix.toIn(); System.out.println("Infix: "+inFix); System.out.println(); } if (user == 3) {System.out.println("Only works with single digit integers (uses char)..."); System.out.println("Solution: "+fix.solvePost()); System.out.println(); } if (user != 1 && user != 2 && user != 3) valid = false; if (user == 4) valid = true; } catch(InputMismatchException e) {valid = false; } } System.out.println("Thanks!"); } }